    ['id' => 2, 'title' => 'Family Therapy', 'link' => 'https://www.psychologytoday.com/intl/therapy-types/family-systems-therapy', 'image' => 'data:image/jpeg;base64,'],
    ['id' => 3, 'title' => 'Business Psychology', 'link' => 'https://theabp.org.uk/about/what-is-business-psychology/', 'image' => 'data:image/jpeg;base64,'],
    ['id' => 4, 'title' => 'Social Psychology', 'link' => 'https://www.simplypsychology.org/social-psychology.html', 'image' => 'data:image/jpeg;base64,'],
    ['id' => 5, 'title' => 'Cognitive Behavioral Therapy (CBT) Guide', 'link' => 'https://www.psychologytools.com/resources/cognitive-behavioral-therapy-cbt-guide/', 'image' => 'data:image/jpeg;base64,'],
